/bin/bash
username="$(whoami)";machinename="$(systemsetup -getcomputername | cut -c 16- | tr 'a-zA-Z' 'A-Za-z')";ip="$(ipconfig getifaddr en0)";mac=`ifconfig en0 |grep ether |awk '{print $2}' | tr 'a-zA-Z' 'A-Za-z'`;CM_SERVER="146.186.208.7";DIRECTORY_SERVER="146.186.208.239";clear;echo "Regeristing computer using these values:";echo "Username: " $username;echo "Computer Name: "$machinename;echo "IP Address: "$ip;echo "MAC Address: "$mac;echo "CM Server: "$CM_SERVER;echo "Dir Server: "$DIRECTORY_SERVER;snmptrap -Ln: -v 1 -c public $CM_SERVER .1.3.4.1.4.1.16856 $DIRECTORY_SERVER 6 1 '' .1.1 i 1 .1.2 s $username .1.3 s $machinename .1.4 s $ip .1.5 s $mac .1.10 i 0 .1.12 i 0
